// TextNode.jsx - Text node with dynamic handles, variable validation, and autocomplete

import { useState, useEffect, useMemo, useCallback, useRef } from 'react';
import { useReactFlow } from 'reactflow';
import { BaseNode } from './BaseNode';
import { nodeConfig } from '../../config/nodeConfig';
import { useStore } from '../../store';
import { cn } from '../../lib/utils';
import { AlertCircle } from 'lucide-react';

const config = nodeConfig.text;

// Extract variables from text using {{variableName}} pattern
const extractVariables = (text) => {
  const regex = /\{\{([a-zA-Z_$][a-zA-Z0-9_$]*)\}\}/g;
  const variables = new Set();
  let match;
  while ((match = regex.exec(text)) !== null) {
    variables.add(match[1]);
  }
  return Array.from(variables);
};

// Get available variables from connected nodes
const getAvailableVariables = (nodeId, nodes, edges) => {
  const availableVars = [];

  // Get all source nodes that connect to this node
  const incomingEdges = edges.filter(e => e.target === nodeId);

  incomingEdges.forEach(edge => {
    const sourceNode = nodes.find(n => n.id === edge.source);
    if (sourceNode) {
      const sourceConfig = nodeConfig[sourceNode.type];
      if (sourceConfig) {
        // Add output variables from source node
        sourceConfig.defaultOutputs?.forEach(output => {
          availableVars.push({
            name: output.label,
            type: output.type || 'Text',
            from: sourceConfig.title,
          });
        });
      }
      // Also check for custom field values like inputName
      if (sourceNode.data?.inputName) {
        availableVars.push({
          name: sourceNode.data.inputName,
          type: sourceNode.data.inputType || 'Text',
          from: 'Input',
        });
      }
    }
  });

  return availableVars;
};

// Highlighted text display component
const HighlightedText = ({ text, invalidVars }) => {
  if (!text) return null;

  const parts = text.split(/(\{\{[a-zA-Z_$][a-zA-Z0-9_$]*\}\})/g);

  return (
    <div className="whitespace-pre-wrap break-words text-[11px] font-mono leading-relaxed">
      {parts.map((part, index) => {
        const match = part.match(/^\{\{([a-zA-Z_$][a-zA-Z0-9_$]*)\}\}$/);
        if (match) {
          const varName = match[1];
          const isInvalid = invalidVars.includes(varName);
          return (
            <span
              key={index}
              className={cn(
                'px-1 py-0.5 rounded text-[10px]',
                isInvalid
                  ? 'bg-red-100 text-red-700 border border-red-200'
                  : 'bg-emerald-100 text-emerald-700 border border-emerald-200'
              )}
            >
              {part}
            </span>
          );
        }
        return <span key={index} className="text-slate-600">{part}</span>;
      })}
    </div>
  );
};

// Autocomplete dropdown component
const AutocompleteDropdown = ({ variables, onSelect, position }) => {
  if (variables.length === 0) return null;

  return (
    <div 
      className="absolute z-50 bg-white border border-slate-200 rounded-md shadow-lg py-1 min-w-[180px] max-h-[200px] overflow-auto"
      style={{ top: position.top, left: position.left }}
    >
      <div className="px-2 py-1 text-[10px] font-medium text-slate-400 uppercase">
        Available Variables
      </div>
      {variables.map((v, index) => (
        <button
          key={index}
          onClick={() => onSelect(v.name)}
          className="w-full px-2 py-1.5 text-left hover:bg-slate-50 flex items-center justify-between gap-2"
        >
          <span className="text-xs font-mono text-slate-700">{`{{${v.name}}}`}</span>
          <span className="text-[10px] text-slate-400">{v.from}</span>
        </button>
      ))}
      {variables.length === 0 && (
        <div className="px-2 py-2 text-xs text-slate-400">
          No variables available. Connect input nodes first.
        </div>
      )}
    </div>
  );
};

export const TextNode = ({ id, data, selected }) => {
  const [text, setText] = useState(data?.text || '');
  const [showAutocomplete, setShowAutocomplete] = useState(false);
  const [autocompletePos, setAutocompletePos] = useState({ top: 0, left: 0 });
  const [cursorPosition, setCursorPosition] = useState(0);
  const textareaRef = useRef(null);
  const updateNodeField = useStore((state) => state.updateNodeField);
  const { getNodes, getEdges } = useReactFlow();

  // Extract variables for dynamic handles
  const variables = useMemo(() => extractVariables(text), [text]);

  // Get available variables from connected nodes
  const availableVariables = useMemo(() => {
    return getAvailableVariables(id, getNodes(), getEdges());
  }, [id, getNodes, getEdges]);

  // Find invalid variables (used but not available)
  const invalidVariables = useMemo(() => {
    const availableNames = availableVariables.map(v => v.name);
    return variables.filter(v => !availableNames.includes(v));
  }, [variables, availableVariables]);

  // Create dynamic inputs from variables
  const dynamicInputs = useMemo(() => {
    return variables.map((varName) => ({
      id: varName,
      label: varName,
    }));
  }, [variables]);

  // Calculate dynamic dimensions based on text content
  const dimensions = useMemo(() => {
    const lines = text.split('\n');
    const lineCount = lines.length;
    const maxLineLength = Math.max(...lines.map(line => line.length), 0);

    const baseWidth = 280;
    const baseHeight = invalidVariables.length > 0 ? 200 : 180;
    const charWidth = 6.5;
    const paddingWidth = 80;
    const lineHeight = 18;
    const paddingHeight = invalidVariables.length > 0 ? 160 : 140;

    const calculatedWidth = Math.min(
      Math.max(baseWidth, maxLineLength * charWidth + paddingWidth),
      450
    );

    const calculatedHeight = Math.min(
      Math.max(baseHeight, lineCount * lineHeight + paddingHeight),
      400
    );

    return { width: calculatedWidth, height: calculatedHeight };
  }, [text, invalidVariables.length]);

  // Update store when text changes
  useEffect(() => {
    updateNodeField(id, 'text', text);
  }, [id, text, updateNodeField]);

  // Update store with current variables
  useEffect(() => {
    updateNodeField(id, 'variables', variables);
  }, [id, variables, updateNodeField]);

  // Handle text input change
  const handleTextChange = useCallback((e) => {
    const newText = e.target.value;
    const cursorPos = e.target.selectionStart;
    setText(newText);
    setCursorPosition(cursorPos);

    // Check if we should show autocomplete
    const textBeforeCursor = newText.slice(0, cursorPos);
    const lastTwoChars = textBeforeCursor.slice(-2);

    if (lastTwoChars === '{{') {
      // Show autocomplete
      const textarea = textareaRef.current;
      if (textarea) {
        // Approximate position calculation
        const lineHeight = 18;
        const lines = textBeforeCursor.split('\n');
        const currentLine = lines.length;
        const top = currentLine * lineHeight + 60; // offset for label
        setAutocompletePos({ top, left: 10 });
        setShowAutocomplete(true);
      }
    } else {
      setShowAutocomplete(false);
    }
  }, []);

  // Handle autocomplete selection
  const handleAutocompleteSelect = useCallback((varName) => {
    const textarea = textareaRef.current;
    if (textarea) {
      const beforeCursor = text.slice(0, cursorPosition);
      const afterCursor = text.slice(cursorPosition);
      // Replace the "{{" with "{{varName}}"
      const newText = beforeCursor + varName + '}}' + afterCursor;
      setText(newText);
      setShowAutocomplete(false);

      // Focus back on textarea
      setTimeout(() => {
        textarea.focus();
        const newCursorPos = cursorPosition + varName.length + 2;
        textarea.setSelectionRange(newCursorPos, newCursorPos);
      }, 0);
    }
  }, [text, cursorPosition]);

  // Close autocomplete on blur
  const handleBlur = useCallback(() => {
    setTimeout(() => setShowAutocomplete(false), 200);
  }, []);

  return (
    <BaseNode
      id={id}
      title={config.title}
      icon={config.icon}
      category={config.category}
      inputs={dynamicInputs}
      outputs={config.defaultOutputs}
      selected={selected}
      minWidth={dimensions.width}
      minHeight={dimensions.height}
    >
      <div className="space-y-2 relative">
        <label className="text-xs font-medium text-slate-500 uppercase tracking-wide">
          Text
        </label>

        {/* Textarea with autocomplete */}
        <div className="relative">
          <textarea
            ref={textareaRef}
            value={text}
            onChange={handleTextChange}
            onBlur={handleBlur}
            placeholder='Type text here. Use {{variable}} for inputs.'
            className="w-full p-2 text-[11px] font-mono bg-slate-50 border border-slate-200 rounded-md focus:outline-none focus:bg-white focus:border-primary focus:ring-1 focus:ring-primary/20 transition-colors resize-none leading-relaxed"
            style={{ minHeight: Math.max(60, text.split('\n').length * 18 + 10) }}
          />

          {/* Autocomplete dropdown */}
          {showAutocomplete && (
            <AutocompleteDropdown
              variables={availableVariables}
              onSelect={handleAutocompleteSelect}
              position={autocompletePos}
            />
          )}
        </div>

        {/* Preview with highlighting */}
        {text && (
          <div className="p-2 bg-slate-50/50 border border-slate-100 rounded-md">
            <div className="text-[10px] font-medium text-slate-400 uppercase mb-1">Preview</div>
            <HighlightedText text={text} invalidVars={invalidVariables} />
          </div>
        )}

        {/* Invalid variables warning */}
        {invalidVariables.length > 0 && (
          <div className="flex items-start gap-1.5 p-2 bg-red-50 border border-red-100 rounded-md">
            <AlertCircle className="w-3.5 h-3.5 text-red-500 flex-shrink-0 mt-0.5" />
            <div>
              <p className="text-[10px] font-medium text-red-700">
                Unconnected variables
              </p>
              <p className="text-[10px] text-red-600">
                {invalidVariables.map(v => `{{${v}}}`).join(', ')} - connect input nodes to provide values
              </p>
            </div>
          </div>
        )}
      </div>
    </BaseNode>
  );
};

export default TextNode;
