// BaseNode.jsx - Flexible node container with category colors and dynamic handles

import { Handle, Position } from 'reactflow';
import { cn } from '../../lib/utils';

// Category color mapping
const categoryColors = {
  input: {
    border: 'border-l-blue-500',
    bg: 'bg-blue-500/5',
    header: 'bg-blue-500/10',
    icon: 'text-blue-500',
  },
  output: {
    border: 'border-l-violet-500',
    bg: 'bg-violet-500/5',
    header: 'bg-violet-500/10',
    icon: 'text-violet-500',
  },
  ai: {
    border: 'border-l-cyan-500',
    bg: 'bg-cyan-500/5',
    header: 'bg-cyan-500/10',
    icon: 'text-cyan-500',
  },
  logic: {
    border: 'border-l-amber-500',
    bg: 'bg-amber-500/5',
    header: 'bg-amber-500/10',
    icon: 'text-amber-500',
  },
  data: {
    border: 'border-l-emerald-500',
    bg: 'bg-emerald-500/5',
    header: 'bg-emerald-500/10',
    icon: 'text-emerald-500',
  },
  integration: {
    border: 'border-l-pink-500',
    bg: 'bg-pink-500/5',
    header: 'bg-pink-500/10',
    icon: 'text-pink-500',
  },
};

// Custom handle component with tooltip support
const NodeHandle = ({ type, position, id, label, style, className }) => {
  return (
    <div className="group relative">
      <Handle
        type={type}
        position={position}
        id={id}
        style={style}
        className={cn(
          '!w-3 !h-3 !border-2 !border-white !bg-gray-400 hover:!bg-primary transition-colors',
          type === 'source' ? '!bg-primary' : '!bg-gray-400',
          className
        )}
      />
      {label && (
        <span
          className={cn(
            'absolute text-[10px] text-muted-foreground whitespace-nowrap opacity-0 group-hover:opacity-100 transition-opacity pointer-events-none',
            position === Position.Left ? 'left-4 top-1/2 -translate-y-1/2' : 'right-4 top-1/2 -translate-y-1/2'
          )}
        >
          {label}
        </span>
      )}
    </div>
  );
};

export const BaseNode = ({
  id,
  title,
  icon: Icon,
  category = 'data',
  inputs = [],
  outputs = [],
  children,
  selected,
  className,
  minWidth = 220,
  minHeight,
}) => {
  const colors = categoryColors[category] || categoryColors.data;

  // Calculate handle positions
  const getHandlePosition = (index, total) => {
    if (total === 1) return '50%';
    const spacing = 100 / (total + 1);
    return `${spacing * (index + 1)}%`;
  };

  return (
    <div
      className={cn(
        'relative bg-card rounded-lg border border-border shadow-node transition-shadow',
        'border-l-4',
        colors.border,
        selected && 'shadow-node-selected ring-2 ring-primary/20',
        className
      )}
      style={{ minWidth, minHeight }}
    >
      {/* Input Handles */}
      {inputs.map((input, index) => (
        <NodeHandle
          key={input.id}
          type="target"
          position={Position.Left}
          id={`${id}-${input.id}`}
          label={input.label}
          style={{ top: getHandlePosition(index, inputs.length) }}
        />
      ))}

      {/* Header */}
      <div className={cn('flex items-center gap-2 px-3 py-2 rounded-t-lg border-b border-border', colors.header)}>
        {Icon && <Icon className={cn('w-4 h-4', colors.icon)} />}
        <span className="text-sm font-medium text-foreground">{title}</span>
      </div>

      {/* Body */}
      <div className="p-3 space-y-3">
        {children}
      </div>

      {/* Output Handles */}
      {outputs.map((output, index) => (
        <NodeHandle
          key={output.id}
          type="source"
          position={Position.Right}
          id={`${id}-${output.id}`}
          label={output.label}
          style={{ top: getHandlePosition(index, outputs.length) }}
        />
      ))}
    </div>
  );
};

export default BaseNode;
