import React, { useState, useEffect } from 'react';
import { Globe } from 'lucide-react';
import { BaseNode } from './BaseNode';
import { TextField, TextAreaField, SelectField } from '../fields';
import { useStore } from '../../store';

export const APINode = ({ id, data, selected }) => {
  const updateNodeField = useStore((state) => state.updateNodeField);

  const [url, setUrl] = useState(data?.url || '');
  const [method, setMethod] = useState(data?.method || 'GET');
  const [headers, setHeaders] = useState(data?.headers || '');

  // Sync with store
  useEffect(() => {
    updateNodeField(id, 'url', url);
  }, [id, url, updateNodeField]);

  useEffect(() => {
    updateNodeField(id, 'method', method);
  }, [id, method, updateNodeField]);

  useEffect(() => {
    updateNodeField(id, 'headers', headers);
  }, [id, headers, updateNodeField]);

  return (
    <BaseNode
      id={id}
      title="API Request"
      icon={<Globe size={16} />}
      category="integration"
      inputs={[{ id: 'body', label: 'Body' }]}
      outputs={[{ id: 'response', label: 'Response' }]}
      selected={selected}
    >
      <TextField
        label="URL"
        value={url}
        onChange={setUrl}
        placeholder="https://api.example.com/endpoint"
      />
      <SelectField
        label="Method"
        value={method}
        onChange={setMethod}
        options={['GET', 'POST', 'PUT', 'DELETE', 'PATCH']}
      />
      <TextAreaField
        label="Headers (JSON)"
        value={headers}
        onChange={setHeaders}
        placeholder='{"Authorization": "Bearer ..."}'
        rows={2}
      />
    </BaseNode>
  );
};

export default APINode;
