import React, { useState, useEffect } from 'react';
import { GitBranch } from 'lucide-react';
import { BaseNode } from './BaseNode';
import { SelectField } from '../fields';
import { useStore } from '../../store';

export const ConditionalNode = ({ id, data, selected }) => {
  const updateNodeField = useStore((state) => state.updateNodeField);

  const [operator, setOperator] = useState(data?.operator || 'equals');

  // Sync with store
  useEffect(() => {
    updateNodeField(id, 'operator', operator);
  }, [id, operator, updateNodeField]);

  const operatorOptions = [
    { value: 'equals', label: 'Equals' },
    { value: 'not_equals', label: 'Not Equals' },
    { value: 'contains', label: 'Contains' },
    { value: 'greater_than', label: 'Greater Than' },
    { value: 'less_than', label: 'Less Than' },
    { value: 'is_empty', label: 'Is Empty' },
  ];

  return (
    <BaseNode
      id={id}
      title="Conditional"
      icon={<GitBranch size={16} />}
      category="logic"
      inputs={[
        { id: 'value_a', label: 'Value A' },
        { id: 'value_b', label: 'Value B' },
      ]}
      outputs={[
        { id: 'true', label: 'True' },
        { id: 'false', label: 'False' },
      ]}
      selected={selected}
    >
      <SelectField
        label="Operator"
        value={operator}
        onChange={setOperator}
        options={operatorOptions}
      />
      <p className="text-[10px] text-muted-foreground mt-1">
        Compare Value A with Value B using the selected operator
      </p>
    </BaseNode>
  );
};

export default ConditionalNode;
