// draggableNode.js

import { cn } from './lib/utils';

export const DraggableNode = ({ type, label, icon: Icon, category, categoryColor }) => {
  const onDragStart = (event, nodeType) => {
    const appData = { nodeType };
    event.target.style.cursor = 'grabbing';
    event.dataTransfer.setData('application/reactflow', JSON.stringify(appData));
    event.dataTransfer.effectAllowed = 'move';
  };

  return (
    <div
      className={cn(
        "flex items-center gap-2 px-3 py-2 rounded-lg cursor-grab",
        "bg-white hover:bg-gray-50",
        "transition-all duration-200 hover:shadow-sm",
        "select-none"
      )}
      style={{ 
        border: '1px solid #e5e7eb',
        color: '#374151'
      }}
      onDragStart={(event) => onDragStart(event, type)}
      onDragEnd={(event) => (event.target.style.cursor = 'grab')}
      draggable
    >
      {Icon && (
        <span className={cn("p-1 rounded", categoryColor)}>
          <Icon size={14} />
        </span>
      )}
      <span className="text-sm font-medium" style={{ color: '#374151' }}>{label}</span>
    </div>
  );
};
