import React, { useState, useEffect } from 'react';
import { Shuffle } from 'lucide-react';
import { BaseNode } from './BaseNode';
import { TextField, SelectField } from '../fields';
import { useStore } from '../../store';

export const TransformNode = ({ id, data, selected }) => {
  const updateNodeField = useStore((state) => state.updateNodeField);

  const [transformType, setTransformType] = useState(data?.transformType || 'uppercase');
  const [expression, setExpression] = useState(data?.expression || '');

  // Sync with store
  useEffect(() => {
    updateNodeField(id, 'transformType', transformType);
  }, [id, transformType, updateNodeField]);

  useEffect(() => {
    updateNodeField(id, 'expression', expression);
  }, [id, expression, updateNodeField]);

  const transformOptions = [
    { value: 'uppercase', label: 'Uppercase' },
    { value: 'lowercase', label: 'Lowercase' },
    { value: 'trim', label: 'Trim Whitespace' },
    { value: 'json_parse', label: 'Parse JSON' },
    { value: 'json_stringify', label: 'Stringify JSON' },
    { value: 'custom', label: 'Custom Expression' },
  ];

  return (
    <BaseNode
      id={id}
      title="Transform"
      icon={<Shuffle size={16} />}
      category="data"
      inputs={[{ id: 'input', label: 'Input' }]}
      outputs={[{ id: 'output', label: 'Output' }]}
      selected={selected}
    >
      <SelectField
        label="Transform Type"
        value={transformType}
        onChange={setTransformType}
        options={transformOptions}
      />
      {transformType === 'custom' && (
        <TextField
          label="Expression"
          value={expression}
          onChange={setExpression}
          placeholder='e.g., value.split(",")'
        />
      )}
    </BaseNode>
  );
};

export default TransformNode;
