// BaseNode.jsx - Clean, professional base node component

import { Handle, Position } from 'reactflow';
import { cn } from '../../lib/utils';
import { nodeConfig } from '../../config/nodeConfig';
import { Settings, MoreHorizontal } from 'lucide-react';

export const BaseNode = ({
  id,
  title,
  icon: Icon,
  category,
  inputs = [],
  outputs = [],
  children,
  selected,
  minWidth = 240,
  minHeight = 'auto',
}) => {
  const config = Object.values(nodeConfig).find(c => c.title === title);
  const iconColorClass = config?.iconColorClass || 'text-slate-500';

  return (
    <div
      className={cn(
        'bg-white rounded-lg border shadow-sm transition-all duration-150',
        selected 
          ? 'border-primary ring-1 ring-primary/20' 
          : 'border-slate-200 hover:border-slate-300'
      )}
      style={{ minWidth, minHeight }}
    >
      {/* Header */}
      <div className="flex items-center justify-between px-3 py-2 border-b border-slate-100 bg-slate-50/50 rounded-t-lg">
        <div className="flex items-center gap-2">
          {Icon && <Icon className={cn('w-4 h-4', iconColorClass)} />}
          <span className="text-sm font-medium text-slate-700">{title}</span>
        </div>
        <div className="flex items-center gap-1">
          <button className="p-1 rounded hover:bg-slate-100 text-slate-400 hover:text-slate-600 transition-colors">
            <Settings className="w-3.5 h-3.5" />
          </button>
          <button className="p-1 rounded hover:bg-slate-100 text-slate-400 hover:text-slate-600 transition-colors">
            <MoreHorizontal className="w-3.5 h-3.5" />
          </button>
        </div>
      </div>

      {/* Content */}
      <div className="p-3 space-y-3">
        {children}
      </div>

      {/* Input Handles */}
      {inputs.map((input, index) => (
        <Handle
          key={input.id}
          type="target"
          position={Position.Left}
          id={input.id}
          className="!w-2.5 !h-2.5 !bg-slate-400 !border-2 !border-white"
          style={{
            top: `${((index + 1) / (inputs.length + 1)) * 100}%`,
          }}
          title={input.label}
        />
      ))}

      {/* Output Handles */}
      {outputs.map((output, index) => (
        <Handle
          key={output.id}
          type="source"
          position={Position.Right}
          id={output.id}
          className="!w-2.5 !h-2.5 !bg-primary !border-2 !border-white"
          style={{
            top: `${((index + 1) / (outputs.length + 1)) * 100}%`,
          }}
          title={output.label}
        />
      ))}
    </div>
  );
};
