// toolbar.js

import { DraggableNode } from './draggableNode';
import { 
  FileInput, 
  FileOutput, 
  Brain, 
  Type,
  Globe,
  GitBranch,
  Shuffle,
  Merge,
  Filter,
} from 'lucide-react';

// Node definitions with categories
const nodeDefinitions = [
  // Start category
  { type: 'customInput', label: 'Input', icon: FileInput, category: 'input' },
  { type: 'customOutput', label: 'Output', icon: FileOutput, category: 'output' },

  // AI category
  { type: 'llm', label: 'LLM', icon: Brain, category: 'ai' },

  // Data category
  { type: 'text', label: 'Text', icon: Type, category: 'data' },
  { type: 'transform', label: 'Transform', icon: Shuffle, category: 'data' },
  { type: 'merge', label: 'Merge', icon: Merge, category: 'data' },

  // Logic category
  { type: 'conditional', label: 'Conditional', icon: GitBranch, category: 'logic' },
  { type: 'filter', label: 'Filter', icon: Filter, category: 'logic' },

  // Integration category
  { type: 'api', label: 'API', icon: Globe, category: 'integration' },
];

// Category colors for badges (using inline styles for reliability)
const categoryStyles = {
  input: { backgroundColor: 'rgba(59, 130, 246, 0.1)', color: '#2563eb' },
  output: { backgroundColor: 'rgba(139, 92, 246, 0.1)', color: '#7c3aed' },
  ai: { backgroundColor: 'rgba(6, 182, 212, 0.1)', color: '#0891b2' },
  data: { backgroundColor: 'rgba(16, 185, 129, 0.1)', color: '#059669' },
  logic: { backgroundColor: 'rgba(245, 158, 11, 0.1)', color: '#d97706' },
  integration: { backgroundColor: 'rgba(236, 72, 153, 0.1)', color: '#db2777' },
};

export const PipelineToolbar = () => {
  return (
    <div style={{ backgroundColor: 'white', borderBottom: '1px solid #e5e7eb', padding: '12px 16px' }}>
      <div style={{ display: 'flex', alignItems: 'center', gap: '8px', flexWrap: 'wrap' }}>
        {nodeDefinitions.map((node) => (
          <DraggableNode
            key={node.type}
            type={node.type}
            label={node.label}
            icon={node.icon}
            category={node.category}
            categoryStyle={categoryStyles[node.category]}
          />
        ))}
      </div>
    </div>
  );
};
