// toolbar.js

import { DraggableNode } from './draggableNode';
import { nodeConfig } from './config/nodeConfig';
import { Tabs, TabsList, TabsTrigger, TabsContent } from './components/ui/tabs';

// Group nodes by category
const categories = {
  general: {
    label: 'General',
    nodes: ['customInput', 'customOutput', 'text'],
  },
  ai: {
    label: 'LLMs',
    nodes: ['llm'],
  },
  data: {
    label: 'Data',
    nodes: ['transform', 'merge'],
  },
  logic: {
    label: 'Logic',
    nodes: ['conditional', 'filter'],
  },
  integration: {
    label: 'Integrations',
    nodes: ['api'],
  },
};

export const PipelineToolbar = () => {
  return (
    <div className="flex-shrink-0 bg-white border-b border-gray-200">
      <Tabs defaultValue="general" className="w-full">
        {/* Category tabs */}
        <div className="px-4 border-b border-gray-100">
          <TabsList className="h-10 bg-transparent p-0 gap-6">
            {Object.entries(categories).map(([key, { label }]) => (
              <TabsTrigger
                key={key}
                value={key}
                className="h-10 px-0 rounded-none border-b-2 border-transparent data-[state=active]:border-primary data-[state=active]:bg-transparent data-[state=active]:shadow-none text-gray-500 data-[state=active]:text-gray-900 font-medium text-sm"
              >
                {label}
              </TabsTrigger>
            ))}
          </TabsList>
        </div>

        {/* Node cards */}
        {Object.entries(categories).map(([key, { nodes }]) => (
          <TabsContent key={key} value={key} className="mt-0 px-4 py-3">
            <div className="flex flex-wrap gap-3">
              {nodes.map((type) => {
                const config = nodeConfig[type];
                if (!config) return null;
                return (
                  <DraggableNode
                    key={type}
                    type={type}
                    label={config.title}
                    icon={config.icon}
                  />
                );
              })}
            </div>
          </TabsContent>
        ))}
      </Tabs>
    </div>
  );
};
