// toolbar.js

import { DraggableNode } from './draggableNode';
import { nodeConfig } from './config/nodeConfig';
import { Tabs, TabsList, TabsTrigger, TabsContent } from './components/ui/tabs';

// Group nodes by category with display names
const categories = {
  all: {
    label: 'All Nodes',
    nodes: ['customInput', 'customOutput', 'llm', 'text', 'transform', 'merge', 'conditional', 'filter', 'api'],
  },
  start: {
    label: 'Start',
    nodes: ['customInput'],
  },
  ai: {
    label: 'AI',
    nodes: ['llm'],
  },
  data: {
    label: 'Data',
    nodes: ['text', 'transform', 'merge'],
  },
  logic: {
    label: 'Logic',
    nodes: ['conditional', 'filter'],
  },
  integration: {
    label: 'Integration',
    nodes: ['api'],
  },
  end: {
    label: 'End',
    nodes: ['customOutput'],
  },
};

export const PipelineToolbar = () => {
  return (
    <div className="flex-shrink-0 bg-white border-b border-border">
      <Tabs defaultValue="all" className="w-full">
        <div className="flex items-center px-4 border-b border-border">
          <TabsList className="h-10 bg-transparent p-0 gap-0">
            {Object.entries(categories).map(([key, { label }]) => (
              <TabsTrigger
                key={key}
                value={key}
                className="relative h-10 px-4 rounded-none border-b-2 border-transparent data-[state=active]:border-primary data-[state=active]:bg-transparent data-[state=active]:shadow-none text-muted-foreground data-[state=active]:text-foreground font-medium text-sm transition-colors"
              >
                {label}
              </TabsTrigger>
            ))}
          </TabsList>
        </div>

        {Object.entries(categories).map(([key, { nodes }]) => (
          <TabsContent key={key} value={key} className="mt-0 p-3">
            <div className="flex flex-wrap gap-2">
              {nodes.map((type) => {
                const config = nodeConfig[type];
                if (!config) return null;
                return (
                  <DraggableNode
                    key={type}
                    type={type}
                    label={config.title}
                    icon={config.icon}
                    category={config.category}
                    description={config.description}
                  />
                );
              })}
            </div>
          </TabsContent>
        ))}
      </Tabs>
    </div>
  );
};
