// draggableNode.js

import { cn } from './lib/utils';

// Category color mapping for borders and accents
const categoryColors = {
  input: 'border-blue-500 hover:bg-blue-50',
  output: 'border-violet-500 hover:bg-violet-50',
  ai: 'border-cyan-500 hover:bg-cyan-50',
  logic: 'border-amber-500 hover:bg-amber-50',
  data: 'border-emerald-500 hover:bg-emerald-50',
  integration: 'border-pink-500 hover:bg-pink-50',
};

const iconColors = {
  input: 'text-blue-500',
  output: 'text-violet-500',
  ai: 'text-cyan-500',
  logic: 'text-amber-500',
  data: 'text-emerald-500',
  integration: 'text-pink-500',
};

export const DraggableNode = ({ type, label, icon: Icon, category = 'data' }) => {
  const onDragStart = (event, nodeType) => {
    const appData = { nodeType };
    event.target.style.cursor = 'grabbing';
    event.dataTransfer.setData('application/reactflow', JSON.stringify(appData));
    event.dataTransfer.effectAllowed = 'move';
  };

  return (
    <div
      className={cn(
        'flex items-center gap-2 px-3 py-2 bg-white rounded-lg border-2 cursor-grab',
        'transition-all duration-200 hover:shadow-md active:scale-95',
        categoryColors[category] || categoryColors.data
      )}
      onDragStart={(event) => onDragStart(event, type)}
      onDragEnd={(event) => (event.target.style.cursor = 'grab')}
      draggable
    >
      {Icon && (
        <Icon
          className={cn('w-4 h-4', iconColors[category] || iconColors.data)}
        />
      )}
      <span className="text-sm font-medium text-gray-700">{label}</span>
    </div>
  );
};
