// toolbar.js

import { DraggableNode } from './draggableNode';
import { 
  FileInput, 
  FileOutput, 
  Brain, 
  Type,
  Globe,
  GitBranch,
  Shuffle,
  Merge,
  Filter,
} from 'lucide-react';

// Node definitions with categories
const nodeDefinitions = [
  // Start category
  { type: 'customInput', label: 'Input', icon: FileInput, category: 'input' },
  { type: 'customOutput', label: 'Output', icon: FileOutput, category: 'output' },

  // AI category
  { type: 'llm', label: 'LLM', icon: Brain, category: 'ai' },

  // Data category
  { type: 'text', label: 'Text', icon: Type, category: 'data' },
  { type: 'transform', label: 'Transform', icon: Shuffle, category: 'data' },
  { type: 'merge', label: 'Merge', icon: Merge, category: 'data' },

  // Logic category
  { type: 'conditional', label: 'Conditional', icon: GitBranch, category: 'logic' },
  { type: 'filter', label: 'Filter', icon: Filter, category: 'logic' },

  // Integration category
  { type: 'api', label: 'API', icon: Globe, category: 'integration' },
];

// Category colors for badges
const categoryColors = {
  input: 'bg-blue-500/10 text-blue-600 border-blue-500/20',
  output: 'bg-violet-500/10 text-violet-600 border-violet-500/20',
  ai: 'bg-cyan-500/10 text-cyan-600 border-cyan-500/20',
  data: 'bg-emerald-500/10 text-emerald-600 border-emerald-500/20',
  logic: 'bg-amber-500/10 text-amber-600 border-amber-500/20',
  integration: 'bg-pink-500/10 text-pink-600 border-pink-500/20',
};

export const PipelineToolbar = () => {
  return (
    <div className="bg-white border-b px-4 py-3">
      <div className="flex items-center gap-2 flex-wrap">
        {nodeDefinitions.map((node) => (
          <DraggableNode
            key={node.type}
            type={node.type}
            label={node.label}
            icon={node.icon}
            category={node.category}
            categoryColor={categoryColors[node.category]}
          />
        ))}
      </div>
    </div>
  );
};
