// SelectField.jsx - Dropdown select with label

import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '../ui/select';
import { Label } from '../ui/label';
import { cn } from '../../lib/utils';

export const SelectField = ({
  label,
  value,
  onChange,
  options = [],
  placeholder = 'Select...',
  className,
  ...props
}) => {
  return (
    <div className={cn('space-y-1.5', className)}>
      {label && (
        <Label className="text-[11px] font-medium text-gray-500 uppercase tracking-wide">
          {label}
        </Label>
      )}
      <Select value={value} onValueChange={onChange} {...props}>
        <SelectTrigger className="h-8 text-sm bg-gray-50/50 border-gray-200 focus:bg-white transition-colors">
          <SelectValue placeholder={placeholder} />
        </SelectTrigger>
        <SelectContent>
          {options.map((option) => (
            <SelectItem
              key={option.value}
              value={option.value}
              className="text-sm"
            >
              {option.label}
            </SelectItem>
          ))}
        </SelectContent>
      </Select>
    </div>
  );
};

export default SelectField;
