// TextNode.jsx - Text node with dynamic handles for variables and syntax highlighting

import { useState, useEffect, useMemo, useCallback, useRef } from 'react';
import { BaseNode } from './BaseNode';
import { Label } from '../ui/label';
import { nodeConfig } from '../../config/nodeConfig';
import { useStore } from '../../store';
import { cn } from '../../lib/utils';

const config = nodeConfig.text;

// Extract variables from text using {{variableName}} pattern
// Valid JS variable: starts with letter, underscore, or $, followed by alphanumeric, underscore, or $
const extractVariables = (text) => {
  const regex = /\{\{([a-zA-Z_$][a-zA-Z0-9_$]*)\}\}/g;
  const variables = new Set();
  let match;
  while ((match = regex.exec(text)) !== null) {
    variables.add(match[1]);
  }
  return Array.from(variables);
};

// Highlight variables in text for display
const highlightVariables = (text) => {
  if (!text) return null;

  const regex = /(\{\{[a-zA-Z_$][a-zA-Z0-9_$]*\}\})/g;
  const parts = text.split(regex);

  return parts.map((part, index) => {
    if (regex.test(part)) {
      // Reset regex lastIndex
      regex.lastIndex = 0;
      return (
        <span
          key={index}
          className="bg-emerald-200/60 text-emerald-800 rounded px-0.5"
        >
          {part}
        </span>
      );
    }
    // Handle line breaks and spaces for proper rendering
    return part.split('\n').map((line, lineIndex, arr) => (
      <span key={`${index}-${lineIndex}`}>
        {line}
        {lineIndex < arr.length - 1 && <br />}
      </span>
    ));
  });
};

// Custom textarea with syntax highlighting overlay
const HighlightedTextArea = ({ value, onChange, placeholder, className }) => {
  const textareaRef = useRef(null);
  const highlightRef = useRef(null);

  // Sync scroll position between textarea and highlight overlay
  const handleScroll = useCallback(() => {
    if (highlightRef.current && textareaRef.current) {
      highlightRef.current.scrollTop = textareaRef.current.scrollTop;
      highlightRef.current.scrollLeft = textareaRef.current.scrollLeft;
    }
  }, []);

  return (
    <div className="relative">
      {/* Highlighted background layer */}
      <div
        ref={highlightRef}
        className={cn(
          'absolute inset-0 p-3 font-mono text-xs leading-relaxed pointer-events-none overflow-hidden whitespace-pre-wrap break-words',
          'text-transparent', // Make text transparent, only show highlights
          className
        )}
        aria-hidden="true"
      >
        {highlightVariables(value) || (
          <span className="text-gray-400">{placeholder}</span>
        )}
      </div>

      {/* Actual textarea */}
      <textarea
        ref={textareaRef}
        value={value}
        onChange={(e) => onChange(e.target.value)}
        onScroll={handleScroll}
        placeholder={placeholder}
        className={cn(
          'w-full p-3 font-mono text-xs leading-relaxed resize-none',
          'bg-transparent border border-gray-200 rounded-md',
          'focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent',
          'placeholder:text-gray-400',
          className
        )}
        style={{ 
          caretColor: '#1f2937',
          minHeight: '80px',
        }}
      />
    </div>
  );
};

export const TextNode = ({ id, data, selected }) => {
  const [text, setText] = useState(data?.text || '{{input}}');
  const updateNodeField = useStore((state) => state.updateNodeField);

  // Extract variables for dynamic handles
  const variables = useMemo(() => extractVariables(text), [text]);

  // Create dynamic inputs from variables
  const dynamicInputs = useMemo(() => {
    return variables.map((varName) => ({
      id: varName,
      label: varName,
    }));
  }, [variables]);

  // Calculate dynamic dimensions based on text content
  const dimensions = useMemo(() => {
    const lines = text.split('\n');
    const lineCount = lines.length;
    const maxLineLength = Math.max(...lines.map(line => line.length), 0);

    // Base dimensions
    const baseWidth = 280;
    const baseHeight = 160;

    // Calculate width based on longest line (approx 6.5px per character for monospace)
    const charWidth = 6.5;
    const paddingWidth = 100; // Account for padding, borders, etc.
    const calculatedWidth = Math.min(
      Math.max(baseWidth, maxLineLength * charWidth + paddingWidth),
      450
    );

    // Calculate height based on line count (approx 18px per line for text-xs)
    const lineHeight = 18;
    const paddingHeight = 140; // Account for header, label, variables section
    const calculatedHeight = Math.min(
      Math.max(baseHeight, lineCount * lineHeight + paddingHeight),
      400
    );

    return { width: calculatedWidth, height: calculatedHeight };
  }, [text]);

  // Calculate textarea rows based on content
  const textareaRows = useMemo(() => {
    const lineCount = text.split('\n').length;
    return Math.max(4, Math.min(lineCount + 1, 12));
  }, [text]);

  // Update store when text changes (debounced effect)
  useEffect(() => {
    updateNodeField(id, 'text', text);
  }, [id, text, updateNodeField]);

  // Update store with current variables
  useEffect(() => {
    updateNodeField(id, 'variables', variables);
  }, [id, variables, updateNodeField]);

  const handleTextChange = useCallback((newText) => {
    setText(newText);
  }, []);

  return (
    <BaseNode
      id={id}
      title={config.title}
      icon={config.icon}
      category={config.category}
      inputs={dynamicInputs}
      outputs={config.defaultOutputs}
      selected={selected}
      minWidth={dimensions.width}
      minHeight={dimensions.height}
    >
      <div className="space-y-1.5">
        <Label className="text-[11px] font-medium text-gray-500 uppercase tracking-wide">
          Text
        </Label>
        <HighlightedTextArea
          value={text}
          onChange={handleTextChange}
          placeholder="Enter text with {{variables}}"
          className={`h-[${textareaRows * 18}px]`}
        />
      </div>

      {/* Variables display */}
      {variables.length > 0 && (
        <div className="pt-3 mt-3 border-t border-gray-100">
          <div className="flex items-center justify-between mb-2">
            <p className="text-[10px] font-medium text-gray-400 uppercase tracking-wide">
              Variables
            </p>
            <span className="text-[10px] font-medium text-emerald-600 bg-emerald-50 px-1.5 py-0.5 rounded">
              {variables.length}
            </span>
          </div>
          <div className="flex flex-wrap gap-1.5">
            {variables.map((v) => (
              <span
                key={v}
                className="inline-flex items-center px-2 py-1 text-[11px] font-medium bg-gradient-to-r from-emerald-50 to-emerald-100/50 text-emerald-700 rounded-md border border-emerald-200 shadow-sm"
              >
                <span className="text-emerald-400 font-normal">{'{'}</span>
                <span className="text-emerald-400 font-normal">{'{'}</span>
                <span className="mx-0.5">{v}</span>
                <span className="text-emerald-400 font-normal">{'}'}</span>
                <span className="text-emerald-400 font-normal">{'}'}</span>
              </span>
            ))}
          </div>
        </div>
      )}

      {/* Help text when no variables */}
      {variables.length === 0 && (
        <p className="text-[10px] text-gray-400 mt-2">
          Use <code className="px-1 py-0.5 bg-gray-100 rounded text-[9px]">{'{{variableName}}'}</code> to create input handles
        </p>
      )}
    </BaseNode>
  );
};

export default TextNode;
