// draggableNode.js

import { cn } from './lib/utils';
import {
  Tooltip,
  TooltipContent,
  TooltipTrigger,
} from './components/ui/tooltip';

// Category color mapping for borders and accents
const categoryStyles = {
  input: {
    border: 'border-blue-400',
    bg: 'bg-blue-50 hover:bg-blue-100',
    icon: 'text-blue-500',
    badge: 'bg-blue-100 text-blue-700',
  },
  output: {
    border: 'border-violet-400',
    bg: 'bg-violet-50 hover:bg-violet-100',
    icon: 'text-violet-500',
    badge: 'bg-violet-100 text-violet-700',
  },
  ai: {
    border: 'border-cyan-400',
    bg: 'bg-cyan-50 hover:bg-cyan-100',
    icon: 'text-cyan-500',
    badge: 'bg-cyan-100 text-cyan-700',
  },
  logic: {
    border: 'border-amber-400',
    bg: 'bg-amber-50 hover:bg-amber-100',
    icon: 'text-amber-500',
    badge: 'bg-amber-100 text-amber-700',
  },
  data: {
    border: 'border-emerald-400',
    bg: 'bg-emerald-50 hover:bg-emerald-100',
    icon: 'text-emerald-500',
    badge: 'bg-emerald-100 text-emerald-700',
  },
  integration: {
    border: 'border-pink-400',
    bg: 'bg-pink-50 hover:bg-pink-100',
    icon: 'text-pink-500',
    badge: 'bg-pink-100 text-pink-700',
  },
};

export const DraggableNode = ({ type, label, icon: Icon, category = 'data', description }) => {
  const styles = categoryStyles[category] || categoryStyles.data;

  const onDragStart = (event, nodeType) => {
    const appData = { nodeType };
    event.target.style.cursor = 'grabbing';
    event.dataTransfer.setData('application/reactflow', JSON.stringify(appData));
    event.dataTransfer.effectAllowed = 'move';
  };

  const nodeContent = (
    <div
      className={cn(
        'group flex items-center gap-2.5 px-3 py-2 rounded-lg border-2 cursor-grab select-none',
        'transition-all duration-200 hover:shadow-md active:scale-95 active:shadow-sm',
        styles.border,
        styles.bg
      )}
      onDragStart={(event) => onDragStart(event, type)}
      onDragEnd={(event) => (event.target.style.cursor = 'grab')}
      draggable
    >
      {Icon && (
        <div className={cn(
          'flex items-center justify-center w-7 h-7 rounded-md',
          styles.badge
        )}>
          <Icon className={cn('w-4 h-4', styles.icon)} />
        </div>
      )}
      <span className="text-sm font-medium text-gray-700 group-hover:text-gray-900">
        {label}
      </span>
    </div>
  );

  if (description) {
    return (
      <Tooltip delayDuration={300}>
        <TooltipTrigger asChild>
          {nodeContent}
        </TooltipTrigger>
        <TooltipContent side="bottom" className="max-w-xs">
          <p className="text-xs">{description}</p>
        </TooltipContent>
      </Tooltip>
    );
  }

  return nodeContent;
};
