import React, { useState, useEffect } from 'react';
import { Merge } from 'lucide-react';
import { BaseNode } from './BaseNode';
import { SelectField } from '../fields';
import { useStore } from '../../store';

export const MergeNode = ({ id, data, selected }) => {
  const updateNodeField = useStore((state) => state.updateNodeField);

  const [strategy, setStrategy] = useState(data?.strategy || 'concat');

  // Sync with store
  useEffect(() => {
    updateNodeField(id, 'strategy', strategy);
  }, [id, strategy, updateNodeField]);

  const strategyOptions = [
    { value: 'concat', label: 'Concatenate' },
    { value: 'array', label: 'As Array' },
    { value: 'object', label: 'As Object' },
    { value: 'first_non_empty', label: 'First Non-Empty' },
  ];

  return (
    <BaseNode
      id={id}
      title="Merge"
      icon={<Merge size={16} />}
      category="data"
      inputs={[
        { id: 'input_1', label: 'Input 1' },
        { id: 'input_2', label: 'Input 2' },
        { id: 'input_3', label: 'Input 3' },
      ]}
      outputs={[{ id: 'output', label: 'Merged' }]}
      selected={selected}
    >
      <SelectField
        label="Merge Strategy"
        value={strategy}
        onChange={setStrategy}
        options={strategyOptions}
      />
      <p className="text-[10px] text-muted-foreground mt-1">
        Combine multiple inputs into a single output
      </p>
    </BaseNode>
  );
};

export default MergeNode;
