// draggableNode.js - Clean, minimal draggable node cards

import { cn } from './lib/utils';

export const DraggableNode = ({ type, label, icon: Icon }) => {
  const onDragStart = (event, nodeType) => {
    const appData = { nodeType };
    event.target.style.cursor = 'grabbing';
    event.dataTransfer.setData('application/reactflow', JSON.stringify(appData));
    event.dataTransfer.effectAllowed = 'move';
  };

  return (
    <div
      className={cn(
        'flex flex-col items-center justify-center gap-1.5 p-3 min-w-[72px]',
        'bg-white border border-gray-200 rounded-lg cursor-grab',
        'hover:border-gray-300 hover:shadow-sm transition-all',
        'active:scale-95'
      )}
      onDragStart={(event) => onDragStart(event, type)}
      onDragEnd={(event) => (event.target.style.cursor = 'grab')}
      draggable
    >
      {Icon && <Icon className="w-5 h-5 text-gray-600" />}
      <span className="text-xs font-medium text-gray-700">{label}</span>
    </div>
  );
};
