import React, { useState, useEffect, useMemo, useCallback } from 'react';
import { Type } from 'lucide-react';
import { BaseNode } from './BaseNode';
import { useStore } from '../../store';
import { Textarea } from '../ui/textarea';
import { Label } from '../ui/label';

/**
 * Extract variables from text (e.g., {{variableName}})
 * Only valid JavaScript variable names are extracted
 */
const extractVariables = (text) => {
  const regex = /\{\{([a-zA-Z_$][a-zA-Z0-9_$]*)\}\}/g;
  const variables = new Set();
  let match;
  while ((match = regex.exec(text)) !== null) {
    variables.add(match[1]);
  }
  return Array.from(variables);
};

/**
 * Calculate dimensions based on text content
 */
const calculateDimensions = (text) => {
  const lines = text.split('\n');
  const lineCount = lines.length;
  const maxLineLength = Math.max(...lines.map(line => line.length));

  // Calculate height: base height + additional height per line
  const baseHeight = 80;
  const heightPerLine = 20;
  const height = Math.min(300, Math.max(baseHeight, baseHeight + (lineCount - 1) * heightPerLine));

  // Calculate width: base width + additional width for long lines
  const baseWidth = 220;
  const charWidth = 7;
  const width = Math.min(400, Math.max(baseWidth, Math.min(400, maxLineLength * charWidth + 40)));

  return { width, height };
};

export const TextNode = ({ id, data, selected }) => {
  const updateNodeField = useStore((state) => state.updateNodeField);

  const [text, setText] = useState(data?.text || '{{input}}');

  // Extract variables from text
  const variables = useMemo(() => extractVariables(text), [text]);

  // Calculate dimensions
  const dimensions = useMemo(() => calculateDimensions(text), [text]);

  // Generate input handles from variables
  const inputs = useMemo(() => 
    variables.map(varName => ({
      id: varName,
      label: varName,
    })),
    [variables]
  );

  // Sync text with store
  useEffect(() => {
    updateNodeField(id, 'text', text);
  }, [id, text, updateNodeField]);

  // Sync variables with store
  useEffect(() => {
    updateNodeField(id, 'variables', variables);
  }, [id, variables, updateNodeField]);

  // Handle text change with auto-resize
  const handleTextChange = useCallback((e) => {
    setText(e.target.value);
  }, []);

  return (
    <BaseNode
      id={id}
      title="Text"
      icon={<Type size={16} />}
      category="data"
      inputs={inputs}
      outputs={[{ id: 'output', label: 'Output' }]}
      selected={selected}
      className="transition-all duration-200"
      style={{ minWidth: dimensions.width }}
    >
      <div className="space-y-1.5">
        <Label className="text-xs text-muted-foreground">
          Text
          {variables.length > 0 && (
            <span className="ml-2 text-primary">
              ({variables.length} variable{variables.length !== 1 ? 's' : ''})
            </span>
          )}
        </Label>
        <Textarea
          value={text}
          onChange={handleTextChange}
          placeholder="Enter text with {{variables}}..."
          className="text-sm min-h-[60px] resize-none font-mono"
          style={{
            minHeight: Math.max(60, dimensions.height - 80),
          }}
        />
      </div>
      {variables.length > 0 && (
        <div className="flex flex-wrap gap-1 mt-2">
          {variables.map(v => (
            <span
              key={v}
              className="text-[10px] px-1.5 py-0.5 bg-primary/10 text-primary rounded"
            >
              {v}
            </span>
          ))}
        </div>
      )}
    </BaseNode>
  );
};

export default TextNode;
