// BaseNode.jsx - Flexible node container with category colors and dynamic handles

import { Handle, Position } from 'reactflow';
import { cn } from '../../lib/utils';
import {
  Tooltip,
  TooltipContent,
  TooltipTrigger,
} from '../ui/tooltip';

// Category color mapping
const categoryColors = {
  input: {
    border: 'border-l-blue-500',
    headerBg: 'bg-gradient-to-r from-blue-500/10 to-blue-500/5',
    headerBorder: 'border-blue-100',
    icon: 'text-blue-600',
    handle: 'bg-blue-500',
  },
  output: {
    border: 'border-l-violet-500',
    headerBg: 'bg-gradient-to-r from-violet-500/10 to-violet-500/5',
    headerBorder: 'border-violet-100',
    icon: 'text-violet-600',
    handle: 'bg-violet-500',
  },
  ai: {
    border: 'border-l-cyan-500',
    headerBg: 'bg-gradient-to-r from-cyan-500/10 to-cyan-500/5',
    headerBorder: 'border-cyan-100',
    icon: 'text-cyan-600',
    handle: 'bg-cyan-500',
  },
  logic: {
    border: 'border-l-amber-500',
    headerBg: 'bg-gradient-to-r from-amber-500/10 to-amber-500/5',
    headerBorder: 'border-amber-100',
    icon: 'text-amber-600',
    handle: 'bg-amber-500',
  },
  data: {
    border: 'border-l-emerald-500',
    headerBg: 'bg-gradient-to-r from-emerald-500/10 to-emerald-500/5',
    headerBorder: 'border-emerald-100',
    icon: 'text-emerald-600',
    handle: 'bg-emerald-500',
  },
  integration: {
    border: 'border-l-pink-500',
    headerBg: 'bg-gradient-to-r from-pink-500/10 to-pink-500/5',
    headerBorder: 'border-pink-100',
    icon: 'text-pink-600',
    handle: 'bg-pink-500',
  },
};

// Custom handle component with tooltip support
const NodeHandle = ({ type, position, id, label, style, category }) => {
  const colors = categoryColors[category] || categoryColors.data;

  const handle = (
    <Handle
      type={type}
      position={position}
      id={id}
      style={style}
      className={cn(
        '!w-3 !h-3 !border-2 !border-white !shadow-sm transition-all duration-200 hover:!scale-125',
        type === 'source' ? `!${colors.handle}` : '!bg-gray-400 hover:!bg-gray-500'
      )}
    />
  );

  if (label) {
    return (
      <Tooltip delayDuration={0}>
        <TooltipTrigger asChild>
          <div className="relative">
            {handle}
          </div>
        </TooltipTrigger>
        <TooltipContent 
          side={position === Position.Left ? 'left' : 'right'} 
          className="text-xs py-1 px-2"
        >
          {label}
        </TooltipContent>
      </Tooltip>
    );
  }

  return handle;
};

export const BaseNode = ({
  id,
  title,
  icon: Icon,
  category = 'data',
  inputs = [],
  outputs = [],
  children,
  selected,
  className,
  minWidth = 240,
  minHeight,
}) => {
  const colors = categoryColors[category] || categoryColors.data;

  // Calculate handle positions
  const getHandlePosition = (index, total) => {
    if (total === 1) return '50%';
    const spacing = 100 / (total + 1);
    return `${spacing * (index + 1)}%`;
  };

  return (
    <div
      className={cn(
        'relative bg-white rounded-xl border shadow-lg transition-all duration-200',
        'border-l-[3px]',
        colors.border,
        selected && 'ring-2 ring-primary/30 shadow-xl',
        'hover:shadow-xl',
        className
      )}
      style={{ minWidth, minHeight }}
    >
      {/* Input Handles */}
      {inputs.map((input, index) => (
        <NodeHandle
          key={input.id}
          type="target"
          position={Position.Left}
          id={`${id}-${input.id}`}
          label={input.label}
          category={category}
          style={{ top: getHandlePosition(index, inputs.length) }}
        />
      ))}

      {/* Header */}
      <div className={cn(
        'flex items-center gap-2.5 px-3 py-2.5 rounded-t-xl border-b',
        colors.headerBg,
        colors.headerBorder
      )}>
        {Icon && (
          <div className={cn(
            'flex items-center justify-center w-6 h-6 rounded-md bg-white shadow-sm',
          )}>
            <Icon className={cn('w-3.5 h-3.5', colors.icon)} />
          </div>
        )}
        <span className="text-sm font-semibold text-gray-800">{title}</span>
      </div>

      {/* Body */}
      <div className="p-3 space-y-3">
        {children}
      </div>

      {/* Output Handles */}
      {outputs.map((output, index) => (
        <NodeHandle
          key={output.id}
          type="source"
          position={Position.Right}
          id={`${id}-${output.id}`}
          label={output.label}
          category={category}
          style={{ top: getHandlePosition(index, outputs.length) }}
        />
      ))}
    </div>
  );
};

export default BaseNode;
